// export default rootReducer;
const initialState = {
  user: {

  },
  post: {

  }
}

//action creator
export const loginAction = (data:any) => {
  return {
    type: 'LOG_IN',
    data: data,
  }
}

export const logoutAction = (data:any) => {
  return {
    type: 'LOG_OUT',
    data: data,
  }
}

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action:any) => {
  switch(action.type){
    case HYDRATE:
      return {...state, ...action.payload};
    default:
      return state;
  }
}

export default rootReducer;
