import React, { useState } from "react";
import {
  DesktopOutlined,
  FileOutlined,
  SmileOutlined,
  RestOutlined,
  UserOutlined,
} from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
import * as style from '../../styles/layout';


const { Header, Content, Sider } = Layout;

interface PropTypes {
  children: any;
}

type MenuItem = Required<MenuProps>['items'][number];

function getItem(
  label: React.ReactNode,
  key: React.Key,
  icon?: React.ReactNode,
  children?: MenuItem[],
): MenuItem {
  return {
    key,
    icon,
    children,
    label,
  } as MenuItem;
}

const items: MenuItem[] = [
  getItem('로그인', '1', <UserOutlined />),
  getItem('한식', '레시피1', <SmileOutlined />, [
    getItem('면', '2'),
    getItem('국', '3'),
    getItem('밥', '4'),
  ]),
  getItem('중식', '5', <SmileOutlined />),
  getItem('양식', '6', <SmileOutlined />),
  getItem('디저트', '레시피2', <RestOutlined />, [
    getItem('구움과자', '7'),
    getItem('빵', '8'),
    getItem('케이크', '9'),
  ]),
];

const AppLayout = ({ children }: PropTypes) => {
  const [collapsed, setCollapsed] = useState(false);
  const {
    token: { colorBgContainer },
  } = theme.useToken();

  return(
    <style.Container style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)}>
        <div className="demo-logo-vertical" />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" items={items} />
      </Sider>
      <Layout>
        <style.Head>
          <div>나만의 레시피</div>
        </style.Head>
        <style.Tab>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>User</Breadcrumb.Item>
            <Breadcrumb.Item>Bill</Breadcrumb.Item>
          </Breadcrumb>
          <div style={{ padding: 24, minHeight: 360, background: colorBgContainer }}>
            {children}
          </div>
        </style.Tab>
      </Layout>
    </style.Container>
  )
}

export default AppLayout;
