// export default rootReducer;
const initialState = {
  name: 'rudals',
  age: 34,
  password: 'babo',
}

//action creator
const changeNickname = (data:any) => {
  return {
    type: 'CHANGE_NICKNAME',
    data: data,
  }
}

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action:any) => {
  switch(action.type){
    case 'CHANGE_NICKNAME':
      return {...state, name: action.data,}
      break;
  }
}

export default rootReducer;
