import { LoadingOutlined, PlusOutlined } from "@ant-design/icons";
import { Button } from "antd";
import React, { useRef, useState } from "react";
import * as style from '../../styles/createStyle';

export const Sumnail = ({ register, imageUrl, setImageUrl, watch }:any) => {
  // 이미지 업로드
  const sumImgInput = useRef<any>();

  // const [logoLoading, setLogoLoading] = useState(false);
console.log(imageUrl)
  const handleImgBtnClick = (e:any) => {
    e.preventDefault();
    sumImgInput.current.click();
  }

  const handleImgRemoveBtn = ()=>{
    setImageUrl('')
  }

  const [imagePreview, setImagePreview] = useState("");
  const image = watch("image");
    useEffect(() => {
      if (image && image.length > 0) {
        const file = image[0];
        setImagePreview(URL.createObjectURL(file));
      }
    }, [image]);

  return (
    <style.SumContainer>
        <input
        {...register("image")}
          id="image"
          ref={sumImgInput}
          type="file"
          className='imgInput'
          accept='image/*'
          // onChange={onImgChange}
          hidden
        />
        {imagePreview ? <div className="afterImg">
            <img src={imagePreview} alt={imagePreview} />
            <div>
              <Button className="deleteImg" onClick={handleImgRemoveBtn}>제거</Button>
            </div>
          </div> : <button onClick={handleImgBtnClick}>
          <div>{logoLoading ? <LoadingOutlined /> : <PlusOutlined />}</div>
          업로드
        </button>}
      </style.SumContainer>
  )
}
