import React, { useRef, useState } from 'react';
import * as style from '../styles/createStyle';
import {
  useForm,
  SubmitHandler,
  UseFormRegister,
  Path,
  useController
} from 'react-hook-form';
import axios from 'axios';
import { useRouter } from 'next/router';
import ButtonModal from '../components/modal/Buttonmodal';

import { ServeSelect } from '../components/create/select';
import { Category } from '../components/create/category';
import { Ingredient } from '../components/create/ingredient';
import { Recipe } from '../components/create/recipe';

import { LoadingOutlined, PlusOutlined } from '@ant-design/icons';
import { message, Upload } from 'antd';
import type { UploadChangeParam } from 'antd/es/upload';
import type { RcFile, UploadFile, UploadProps } from 'antd/es/upload/interface';
export interface ingredient {
  name: string;
}

export interface recipes {
  recipe: string;
}

export interface Inputs {
  serve: number;
  category: string;
  ingredients: ingredient[];
  recipes: recipes[];
  link?: string;
}

export type FormInputProps<TFormValues> = {
  name: Path<TFormValues>;
  register: UseFormRegister<TFormValues>;
};

// 이미지업로드
const getBase64 = (img: RcFile, callback: (url: string) => void) => {
  const reader = new FileReader();
  reader.addEventListener('load', () => callback(reader.result as string));
  reader.readAsDataURL(img);
};

const beforeUpload = (file: RcFile) => {
  const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
  if (!isJpgOrPng) {
    message.error('You can only upload JPG/PNG file!');
  }
  const isLt2M = file.size / 1024 / 1024 < 2;
  if (!isLt2M) {
    message.error('Image must smaller than 2MB!');
  }
  return isJpgOrPng && isLt2M;
};

const CreateRecipe = () => {
  const [sumbmitData, setSubmitData] = useState<Inputs>();
  const [openModal, setOpenModal] = useState(false);
  const router = useRouter();
  const {
    register,
    handleSubmit,
    watch,
    control,
    formState: { isSubmitting, errors },
  } = useForm<Inputs>({
    mode: 'onChange',
    reValidateMode: 'onChange',
    defaultValues: {
      serve: 1,
      category: '',
      ingredients: [{name: ''}],
      recipes: [{ recipe: '' }],
      link: ''
    },
  });
  console.log(sumbmitData)
  //submit
  const onHandleSubmit: SubmitHandler<Inputs> = (data) => {
    console.log(data)
    // axios
    //   .post(`${api}/topics`, sumbmitData, {
      //     headers: {
        //       authorization: `Bearer ${LocalStorage.getItem('accesstoken')}` || `Bearer ${SessionStorage.getItem('accesstoken')}`
        //     }
        //   })
        //   .then((res: AxiosResponse) => {
          //     console.log('요청 성공!', res);
          //     router.push(`/readvote?pid=${res.data.data.topicId}`)
          //   })
          //   .catch((err: AxiosError) => {
            //     console.log('요청 실패!', err.message);
            //   });
          };

  const confirmSubmit: SubmitHandler<Inputs> = (data) =>{
    setSubmitData(data);
    console.log(data)
    setOpenModal((prev) => !prev);
  }

  //serve
  const serveOption = [1, 2, 3, 4, 5, 6];

  // 카테고리
  const { field } = useController({
    name: 'category',
    control: control
  })

  // 이미지 업로드
  const sumImgInput = useRef(null);

  const [logoLoading, setLogoLoading] = useState(false);
  const [imageUrl, setImageUrl] = useState<string>('');
  const onImgInputBtnClick = (e:any) => {
    e.preventDefault();
    sumImgInput.current.click();
  }

  const onImgChange = async (e:any) => {
    setLogoLoading(true);
    const formData = new FormData();
    formData.append('file', e.target.files[0]);
    // const res = await apiClient.post('', formData);
    setLogoLoading(false);
  }
  // const [loading, setLoading] = useState(false);

  // const handleChange: UploadProps['onChange'] = (info: UploadChangeParam<UploadFile>) => {
  //   if (info.file.status === 'uploading') {
  //     setLoading(true);
  //     return;
  //   }
  //   if (info.file.status === 'done') {
  //     // Get this url from response in real world.
  //     getBase64(info.file.originFileObj as RcFile, (url) => {
  //       setLoading(false);
  //       setImageUrl(url);
  //     });
  //   }
  // };
  // //업로드 버튼
  const uploadButton = (
    <div>
      {logoLoading ? <LoadingOutlined /> : <PlusOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  //취소
  //cancle
  const onHandleCancle = () => {
    if (confirm('정말 취소하시겠습니까?')) {
      router.push('/');
    } else {
      console.log();
    }
  };

  return(
  <form onSubmit={handleSubmit(confirmSubmit)}>
    <style.TopContainer>
      <style.SumContainer>
        <input 
          ref={sumImgInput}
          type="file"
          className='imgInput'
          id="sumImg"
          accept='image/*'
          name="file"
          onChange={onImgChange}
          hidden
        />
        {/* {imageUrl ? <img src={imageUrl} alt="avatar" style={{ width: '100%' }} /> : uploadButton} */}
        <button onClick={onImgInputBtnClick}>
          <div>{logoLoading ? <LoadingOutlined /> : <PlusOutlined />}</div>
          업로드
        </button>
      </style.SumContainer>
      <div className='TopRight'>    
        {/* serve */}
        <style.Serve>
          <ServeSelect register={register} options={serveOption} name={'serve'} />
          <div>인분</div>
        </style.Serve>
        {/* 카테고리 */}
        <style.Category>
          <div>카테고리 :</div>
          <Category field={field} />
        </style.Category>
      </div>
    </style.TopContainer>
    {/* 재료추가 */}
    <style.Title>재료</style.Title>
    <Ingredient 
      register={register}
      name={'ingredients'}
      control={control}
      />
    {/* 레시피 */}
    <style.Title>레시피</style.Title>
    <Recipe 
      register={register}
      name={'recipes'}
      control={control}
    />
    {/* 링크 */}
    <style.Title>
      참고 링크
    </style.Title>
    <style.LinkInput 
      id="link"
      {...register(`link`, {
        required: false,
      })} />
    {/* 버튼 */}
    <style.Btns>
      <style.Cancle onClick={onHandleCancle}>취소하기</style.Cancle>
      <style.Submit id="post" type="submit" disabled={isSubmitting}>
        등록하기
      </style.Submit>
    </style.Btns>
    {/* <>
      {openModal ? (
        <ButtonModal
          text={'등록할까요?'}
          confirmFunc={onHandleSubmit}
          setOpenModal={setOpenModal}
        />
      ) : null}
    </> */}
  </form>
  )
}

export default CreateRecipe;
