import React, { useCallback, useRef } from 'react';
import {Form, Input, Button} from 'antd';

const CreateRecipe = () => {
  const imageInput = useRef();

  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);

  return(<div>
    <input className="imageUpload" type="file" multiple hidden ref={imageInput}/>
        <Button className="imageUpload" onClick={onClickImageUpload}>이미지 업로드</Button>
  </div>)
}

export default CreateRecipe;
