// export default rootReducer;
const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  }
}

//action creator
const login = (data:any) => {
  return {
    type: 'LOG-IN',
    data: data,
  }
}

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action:any) => {
  switch(action.type){
    case 'LOG-IN':
      return {...state, name: action.data,}
      break;
  }
}

export default rootReducer;
