import React, { useRef } from "react";
import * as style from '../../styles/createStyle';
import { TreeSelect } from 'antd';

export function Category({ Controller, options, control, category, onChange }: any){

  return (
    <div className="card flex justify-content-center">
        <Controller
            name='category'
            control={control}
            render={(field:any) => (
                <>
                  {console.log(field, category)}
                    <TreeSelect
                      style={{ width: '100%' }}
                      value={category}
                      dropdownStyle={{ maxHeight: 400, overflow: 'auto' }}
                      treeData={options}
                      placeholder="Please select"
                      treeDefaultExpandAll
                      onChange={onChange}
                    />
                </>
            )}
        />
    </div>
  )
}
