import { LoadingOutlined, PlusOutlined } from "@ant-design/icons";
import { Button } from "antd";
import React, { useRef, useState } from "react";
import * as style from '../../styles/createStyle';

export const Sumnail = ({ register }:any) => {
  // 이미지 업로드
  const sumImgInput = useRef<any>();

  const [logoLoading, setLogoLoading] = useState(false);
  const [image, setImage] = useState('');
console.log(image)
  const handleImgBtnClick = (e:any) => {
    e.preventDefault();
    sumImgInput.current.click();
  }

  const onImgChange = async (e:any) => {
    setLogoLoading(true);
    const formData = new FormData();
    formData.append('imgPath', e.target.files[0]);
    setImage(URL.createObjectURL(e.target.files[0]));
    // const res = await apiClient.post('', formData);
    setLogoLoading(false);
  }

  const handleImgRemoveBtn = ()=>{
    setImage('')
  }

  return (
    <style.SumContainer>
        <input
        {...register("imgPath")}
          ref={sumImgInput}
          type="file"
          className='imgInput'
          accept='image/*'
          name="file"
          onChange={onImgChange}
          hidden
        />
        {image ? <div className="afterImg">
            <img src={image} alt={image} />
            <div>
              <Button className="deleteImg" onClick={handleImgRemoveBtn}>제거</Button>
            </div>
          </div> : <button onClick={handleImgBtnClick}>
          <div>{logoLoading ? <LoadingOutlined /> : <PlusOutlined />}</div>
          업로드
        </button>}
      </style.SumContainer>
  )
}
