export function Category({ Controller, options, control, name }: any){
  const toast = useRef(null);
  return (
    <div className="card flex justify-content-center">
      <Toast ref={toast} />
        <Controller
            name={name}
            control={control}
            render={(field:any) => (
                <>
                  {console.log(field, options)}
                    <TreeSelect 
                      aria-label="Options"
                      id={field.name} 
                      value={field.value} 
                      onChange={field.onChange} 
                      inputRef={field.ref} 
                      options={options} 
                      placeholder="카테고리를 선택해주세요" />
                </>
            )}
        />
    </div>
  )
}


