import React, { useRef } from "react";
import * as style from '../../styles/createStyle';
import { Button } from 'primereact/button';
import { classNames } from 'primereact/utils';
import { TreeSelect } from "primereact/treeselect";
import { Toast } from 'primereact/toast';

const treeData = [
  {
    "key": '한식',
    "label": '한식',
    "icon": "pi pi-fw pi-inbox",
    "children": [
      {
        "key": '밥',
        "label": '밥',
      },
      {
        "key": '국',
        "label": '국',
      },
      {
        "key": '면',
        "label": '면',
      },
      {
        "key": '기타',
        "label": '기타',
      },
    ],
  },
  {
    "key": '중식',
    "label": '중식',
  },
  {
    "key": '양식',
    "label": '양식',
  },
  {
    "key": '디저트',
    "label": '디저트',
    "children": [
      {
        "key": '구움과자',
        "label": '구움과자',
      },
      {
        "key": '빵',
        "label": '빵',
      },
      {
        "key": '케이크',
        "label": '케이크',
      },
    ],
  },
  {
    "key": '다이어트식',
    "label": '다이어트식',
  },
];

export function Category({ Controller, control }: any){
  const toast = useRef(null);
  return (
    <>
      <Toast ref={toast} />
        <Controller
            name="category"
            control={control}
            rules={{ required: 'Value is required.' }}
            render={({ field, fieldState }:any) => (
                <>
                    <TreeSelect 
                      id={field.name} 
                      value={field.value} 
                      onChange={field.onChange} 
                      inputRef={field.ref} 
                      options={treeData} 
                      placeholder="Select Item" 
                      className={classNames('w-full md:w-20rem', { 'p-invalid': fieldState.error })} />
                    {/* {getFormErrorMessage(field.name)} */}
                </>
            )}
        />
    </>
  )
}


