import React, { useCallback, useRef, useState } from 'react';
import {Form, Input, Button} from 'antd';
import * as style from '../styles/createStyle'

const CreateRecipe = () => {
  //추가 설명 이미지 미리볼 url 저장해두는 state
  const [contentFileImage, setContentFileImage] = useState('');
  //콘텐츠 이미지 저장해두는 state
  const [contentsImg, setContentsImg] = useState('');

  //이미지 파일 저장
  const changeContentImg = async (e: any) => {
    e.preventDefault();
    setContentFileImage(URL.createObjectURL(e.target.files[0]));
    if (e.target.files) {
      const [file] = e.target.files;
      const options = {
        maxSizeMB: 0.2,
        useWebWorker: true,
      };
      console.log('압축시작');
      // const compressFile = await imageCompression(file, options);
      // const contentFile: File[] = new File([compressFile], "contentImage.JPG");

      // setContentsImg([contentFile]);
      // if (contentsImg) {
      //   setContentsImg([...contentsImg, contentFile]);
      //   }
    }
  };

  //이미지 파일 삭제
  const deletedetailFileImage = (e: any) => {
    e.preventDefault();
    URL.revokeObjectURL(contentFileImage);
    setContentFileImage('');
    setContentsImg('');
  };

  return(
  <div>
    <style.SumContainer>
      {contentFileImage && (
        <style.ContentImg alt="detailImg" src={contentFileImage} />
      )}
      <style.UploadDelete>
        <style.ImgLabel htmlFor="detailImage">
          <div className="noImg">{contentsImg === '' ? '+' : ''}</div>
        </style.ImgLabel>
        <style.SumnailUpload
          name="detailImg"
          id="detailImage"
          type="file"
          accept="image/*"
          onChange={changeContentImg}
        />
        {contentsImg === '' ? (
          ''
        ) : (
          <style.DeleteSumnaeil onClick={(e:any) => deletedetailFileImage(e)}>
            삭제
          </style.DeleteSumnaeil>
        )}
      </style.UploadDelete>
    </style.SumContainer>
  </div>
  )
}

export default CreateRecipe;
