// export default rootReducer;
const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  }
}

//action creator
export const loginAction = (data:any) => {
  return {
    type: 'LOG-IN',
    data: data,
  }
}

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action:any) => {
  switch(action.type){
    case 'LOG-IN':
      return {
        ...state, 
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        }
      }
      break;
  }
}

export default rootReducer;
