import { LoadingOutlined, PlusOutlined } from "@ant-design/icons";
import { Button } from "antd";
import React, { useRef, useState } from "react";
import * as style from '../../styles/createStyle';

export const Sumnail = ({ imagePath }:any) => {
  // 이미지 업로드
  const sumImgInput = useRef<any>();

  const [logoLoading, setLogoLoading] = useState(false);
  const [imageUrl, setImageUrl] = useState<string>('');
console.log(imageUrl)
  const handleImgBtnClick = (e:any) => {
    e.preventDefault();
    sumImgInput.current.click();
  }

  const onImgChange = async (e:any) => {
    setLogoLoading(true);
    const formData = new FormData();
    formData.append('file', e.target.files[0]);
    setImageUrl(e.target.files[0]);
    // const res = await apiClient.post('', formData);
    setLogoLoading(false);
  }

  return (
    <style.SumContainer>
        <input
          ref={sumImgInput}
          type="file"
          className='imgInput'
          accept='image/*'
          name="file"
          onChange={onImgChange}
          hidden
        />
        {imageUrl ? <div>
            <img src={imageUrl} alt={imageUrl} />
            <div>
              <Button>제거</Button>
            </div>
          </div> : <button onClick={handleImgBtnClick}>
          <div>{logoLoading ? <LoadingOutlined /> : <PlusOutlined />}</div>
          업로드
        </button>}
      </style.SumContainer>
  )
}
